import { RouterProvider, createBrowserRouter, createRoutesFromElements, Route } from 'react-router-dom';

import EditEventPage from './pages/EditEvent';
import ErrorPage from './pages/Error';
import EventDetailPage, {
  loader as eventDetailLoader,
  action as deleteEventAction,
} from './pages/EventDetail';
import EventsPage, { loader as eventsLoader } from './pages/Events';
import EventsRootLayout from './pages/EventsRoot';
import HomePage from './pages/Home';
import NewEventPage from './pages/NewEvent';
import RootLayout from './pages/Root';
import { action as manipulateEventAction } from './components/EventForm';
import NewsletterPage, { action as newsletterAction } from './pages/Newsletter';


const router2 = createBrowserRouter(
  createRoutesFromElements(
    <Route path="/" element={<RootLayout />} errorElement={<ErrorPage />}>
      <Route index element={<HomePage />} />
      <Route path='events' element={<EventsRootLayout />}>
        <Route index element={<EventsPage />} loader={eventsLoader} />
        <Route path=':eventId' id='event-detail' loader={eventDetailLoader}>
          <Route index element={<EventDetailPage />} action={deleteEventAction} />
          <Route path='edit' element={<EditEventPage />} action={manipulateEventAction} />
        </Route>
        <Route path='new' element={<NewEventPage />} action={manipulateEventAction} />
      </Route>
      <Route path='newsletter' element={<NewsletterPage />} action={newsletterAction} />
    </Route>
  ));

  const router = createBrowserRouter([
    {
      path: '/',
      element: <RootLayout />,
      errorElement: <ErrorPage />,
      children: [
        { index: true, element: <HomePage /> },
        {
          path: 'events',
          element: <EventsRootLayout />,
          children: [
            {
              index: true,
              element: <EventsPage />,
              loader: eventsLoader,
            },
            {
              path: ':eventId',
              id: 'event-detail',
              loader: eventDetailLoader,
              children: [
                {
                  index: true,
                  element: <EventDetailPage />,
                  action: deleteEventAction,
                },
                {
                  path: 'edit',
                  element: <EditEventPage />,
                  action: manipulateEventAction,
                },
              ],
            },
            {
              path: 'new',
              element: <NewEventPage />,
              action: manipulateEventAction,
            },
          ],
        },
        {
          path: 'newsletter',
          element: <NewsletterPage />,
          action: newsletterAction,
        },
      ],
    },
  ]);

function App() {
  return <RouterProvider router={router2} />;
}

export default App;
